package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Goods;import com.zhoubi.graindepot.mapper.GoodsMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class GoodsBiz extends BaseService<Goods>  {	@Autowired	private GoodsMapper GoodsMapper;	@Override	protected BaseMapper<Goods> getMapper() {		return GoodsMapper;	}	public int checkRepeat(Map map) {		return GoodsMapper.checkRepeat(map);	}}