package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Mechventlog;import com.zhoubi.graindepot.mapper.MechventlogMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class MechventlogBiz extends BaseService<Mechventlog>  {	@Autowired	private MechventlogMapper MechventlogMapper;	@Override	protected BaseMapper<Mechventlog> getMapper() {		return MechventlogMapper;	}	public String getMaxBillcode(Integer graindepotid) {		return MechventlogMapper.getMaxBillcode(graindepotid);	}}