package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Storage;import com.zhoubi.graindepot.bean.Video;import com.zhoubi.graindepot.mapper.StorageMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class StorageBiz extends BaseService<Storage>  {	@Autowired	private StorageMapper StorageMapper;	@Override	protected BaseMapper<Storage> getMapper() {		return StorageMapper;	}	public int updateStoragePosition(Storage item){		return StorageMapper.updateStoragePosition(item);	}	public List<Video> selectVideoList(Map map){		return StorageMapper.selectVideoList(map);	}}