package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Trader;import com.zhoubi.graindepot.mapper.TraderMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class TraderBiz extends BaseService<Trader> {    @Autowired    private TraderMapper TraderMapper;    @Override    protected BaseMapper<Trader> getMapper() {        return TraderMapper;    }    public int checkRepeat(Map map) {        return TraderMapper.checkRepeat(map);    }    public List<Trader> getUnBlankTraderPageList(Map map) {        return TraderMapper.getUnBlankTraderPageList(map);    }    public int getUnBlankTraderPageCount(Map map) {        return TraderMapper.getUnBlankTraderPageCount(map);    }}