package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Truck;import com.zhoubi.graindepot.mapper.TruckMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class TruckBiz extends BaseService<Truck>  {	@Autowired	private TruckMapper TruckMapper;	@Override	protected BaseMapper<Truck> getMapper() {		return TruckMapper;	}	public int checkRepeat(Map map) {		return TruckMapper.checkRepeat(map);	}}